mapper.update:
UPDATE POST_H
SET   CT_SEQ  = #{CT_SEQ},
      FIELD   = #{FIELD},
      TITLE   = #{TITLE},
      CONTENT = #{CONTENT},
      HASHTAG = #{HASHTAG},
      STATE   = #{STATE}
      CO_STATE = #{CO_STATE}
WHERE SEQ     = #{SEQ}

mapper.select:
SELECT TT1.* FROM (SELECT ROWNUM RNUM, T1.*, T2.* FROM ( SELECT SEQ, CT_SEQ, ID, FIELD, TITLE, CONTENT, WDATE, HASHTAG, STATE, CO_STATE FROM POST_H WHERE NOT STATE = '2' AND ID = #{ID}
order by SEQ DESC )T1 NATURAL JOIN ( SELECT COUNT(*) as TOT_CNT FROM POST_H WHERE NOT STATE = '2' AND ID = #{ID}
)T2 )TT1 WHERE RNUM BETWEEN (5 * (#{PAGE_NUM}-1)+1) AND (( 5 * (#{PAGE_NUM}-1))+5 )

mapper.update:
UPDATE POST_H
SET   STATE   = 2
WHERE SEQ     = #{SEQ}
